import React from 'react';
import {
  fireEvent,
  render,
  RenderResult,
  cleanup,
} from '@testing-library/react';
import '@testing-library/jest-dom';
import Image from '../Image';
import { config } from 'react-transition-group';

config.disabled = true;

describe('test Image Component', () => {
  let wrapper: RenderResult;
  beforeEach(() => {
    wrapper = render(
      <Image
        src="https://www.haoduoliao.com/wp-content/uploads/2019/11/wei_xin_jie_tu-2236.jpg"
        width={200}
      />,
      { container: document.body },
    );
  });
  it('component should render correctly', () => {
    expect(wrapper.container.querySelector('.g-image')).toBeInTheDocument();
    expect(wrapper.container.getElementsByTagName('img')[0]).toBeVisible();
    expect(
      wrapper.container.querySelector('.g-image-info-mask'),
    ).not.toBeVisible();
    expect(wrapper.container.getElementsByTagName('img')[0].src).toEqual(
      'https://www.haoduoliao.com/wp-content/uploads/2019/11/wei_xin_jie_tu-2236.jpg',
    );
    expect(wrapper.asFragment()).toMatchSnapshot();
  });
  it('hover should make info to be visible', () => {
    const mask = wrapper.container.querySelector('.g-image-info-mask');
    fireEvent.mouseEnter(mask);
    expect(mask).toBeVisible();
    fireEvent.mouseLeave(mask);
    expect(mask).not.toBeVisible();
  });
  it('click mask should make lightup to be visible', () => {
    const mask = wrapper.container.querySelector('.g-image-info-mask');
    fireEvent.mouseEnter(mask);
    fireEvent.click(mask);
    const lightup = document.querySelector('.g-image-lightup-wrapper');
    expect(lightup).toBeInTheDocument();
    expect(wrapper.asFragment()).toMatchSnapshot();
  });
  it('click mask should close lightup', () => {
    const mask = wrapper.container.querySelector('.g-image-info-mask');
    fireEvent.mouseEnter(mask);
    fireEvent.click(mask);
    const lightup = document.querySelector('.g-image-lightup-wrapper');
    expect(lightup).toBeInTheDocument();
    const lightupMask = wrapper.container.querySelector(
      '.g-image-lightup-mask',
    );
    fireEvent.click(lightupMask);
    expect(lightup).not.toBeInTheDocument();
    expect(wrapper.asFragment()).toMatchSnapshot();
  });
  it('test closeButton', () => {
    const mask = wrapper.container.querySelector('.g-image-info-mask');
    fireEvent.mouseEnter(mask);
    fireEvent.click(mask);
    const lightup = document.querySelector('.g-image-lightup-wrapper');
    expect(lightup).toBeInTheDocument();
    const closeButton = wrapper.container.querySelectorAll(
      '.g-image-option-item',
    )[0];
    expect(closeButton).toBeInTheDocument();
    fireEvent.click(closeButton);
    expect(lightup).not.toBeInTheDocument();
    expect(wrapper.asFragment()).toMatchSnapshot();
  });
  it('test turn right', () => {
    const mask = wrapper.container.querySelector('.g-image-info-mask');
    fireEvent.mouseEnter(mask);
    fireEvent.click(mask);
    const turnRightButton = wrapper.container.querySelectorAll(
      '.g-image-option-item',
    )[1];
    const img = wrapper.container.querySelector('.g-image-lightup-content');
    expect(turnRightButton).toBeInTheDocument();
    fireEvent.click(turnRightButton);
    expect(img).toHaveStyle({ transform: `scale3d(1, 1, 1) rotate(90deg)` });
  });
  it('test turn left', () => {
    const mask = wrapper.container.querySelector('.g-image-info-mask');
    fireEvent.mouseEnter(mask);
    fireEvent.click(mask);
    const turnLeftButton = wrapper.container.querySelectorAll(
      '.g-image-option-item',
    )[2];
    const img = wrapper.container.querySelector('.g-image-lightup-content');
    expect(turnLeftButton).toBeInTheDocument();
    fireEvent.click(turnLeftButton);
    expect(img).toHaveStyle({ transform: `scale3d(1, 1, 1) rotate(-90deg)` });
  });
  it('test enlarge', () => {
    const mask = wrapper.container.querySelector('.g-image-info-mask');
    fireEvent.mouseEnter(mask);
    fireEvent.click(mask);
    const enlargeButton = wrapper.container.querySelectorAll(
      '.g-image-option-item',
    )[3];
    const img = wrapper.container.querySelector('.g-image-lightup-content');
    expect(enlargeButton).toBeInTheDocument();
    for (let i = 1; i <= 10; i++) {
      let num: number = 1;
      num = Math.min(Math.pow(2, i), 16);
      fireEvent.click(enlargeButton);
      expect(img).toHaveStyle({
        transform: `scale3d(${num}, ${num}, 1) rotate(0deg)`,
      });
    }
    const lightupMask = wrapper.container.querySelector(
      '.g-image-lightup-mask',
    );
    fireEvent.click(lightupMask);
    fireEvent.mouseEnter(mask);
    fireEvent.click(mask);
    const newImg = wrapper.container.querySelector('.g-image-lightup-content');
    expect(newImg).toHaveStyle({
      transform: `scale3d(1, 1, 1) rotate(0deg)`,
    });
  });
  it('test zoom', () => {
    const mask = wrapper.container.querySelector('.g-image-info-mask');
    fireEvent.mouseEnter(mask);
    fireEvent.click(mask);
    const zoomButton = wrapper.container.querySelectorAll(
      '.g-image-option-item',
    )[4];
    const img = wrapper.container.querySelector('.g-image-lightup-content');
    expect(zoomButton).toBeInTheDocument();
    for (let i = 1; i <= 10; i++) {
      let num: number = 1;
      num = Math.max(Math.pow(2, -i), 0.125);
      fireEvent.click(zoomButton);
      expect(img).toHaveStyle({
        transform: `scale3d(${num}, ${num}, 1) rotate(0deg)`,
      });
    }
    const lightupMask = wrapper.container.querySelector(
      '.g-image-lightup-mask',
    );
    fireEvent.click(lightupMask);
    fireEvent.mouseEnter(mask);
    fireEvent.click(mask);
    const newImg = wrapper.container.querySelector('.g-image-lightup-content');
    expect(newImg).toHaveStyle({
      transform: `scale3d(1, 1, 1) rotate(0deg)`,
    });
  });
  it('can handle error', () => {
    cleanup();
    wrapper = render(
      <Image
        src="https://www.haoduoliao.com/a.jpg"
        width={200}
        fallback="data:image/png;base64,"
      />,
      { container: document.body },
    );
    expect(wrapper.container.querySelector('.g-image')).toBeInTheDocument();
    const img = wrapper.container.getElementsByTagName('img')[0];
    fireEvent.error(img);
    expect(img).toBeInTheDocument();
    expect(img.src).toEqual(
      'data:image/png;base64,',
    );
    const mask = wrapper.container.querySelector('.g-image-info-mask');
    expect(mask).not.toBeInTheDocument();
    fireEvent.click(img);
    expect(
      wrapper.container.querySelector('.g-image-lightup-wrapper'),
    ).not.toBeInTheDocument();
    expect(wrapper.asFragment()).toMatchSnapshot();
  });
});
